using System;
using System.Collections.Generic;
using System.Drawing;
using System.IO;
using System.Linq;
using System.Text;
using System.Threading.Tasks;
using System.Windows;
using System.Windows.Controls;
using System.Windows.Data;
using System.Windows.Documents;
using System.Windows.Input;
using System.Windows.Media;
using System.Windows.Media.Imaging;
using System.Windows.Navigation;
using System.Windows.Shapes;

namespace Viewer
{
	/// <summary>
	/// MainWindow.xaml에 대한 상호 작용 논리
	/// </summary>
	public partial class MainWindow : Window
	{
		static string _binary = "";

		public static bool ConvertTo(string hexStr, out byte[] bytes)
		{
			bytes = null;
			if (null == hexStr)
			{
				return false;
			}
			int bytesLen = (int)Math.Ceiling(hexStr.Length * 0.5);
			bytes = new byte[((bytesLen > 0) ? bytesLen : 1)];
			for (int i = 0; i < bytesLen; ++i)
			{
				bytes[i] = Convert.ToByte(hexStr.Substring(i * 2, 2), 16);
			}

			return true;
		}

		public MainWindow()
		{
			InitializeComponent();

			if (ConvertTo(_binary, out byte[] bytes))
			{
				var image = new BitmapImage();
				using (var mem = new MemoryStream(bytes))
				{
					mem.Position = 0;
					image.BeginInit();
					image.CreateOptions = BitmapCreateOptions.PreservePixelFormat;
					image.CacheOption = BitmapCacheOption.OnLoad;
					image.UriSource = null;
					image.StreamSource = mem;
					image.EndInit();
				}
				image.Freeze();

				_imgWin.Source = image;
			}

		}
	}
}
